memberdef.initializer:
do                                                                                             \
{                                                                                              \
    if (!(condition)) [[unlikely]]                                                             \
    {                                                                                          \
        NF_ERROR_EXIT("Assertion `" #condition "` failed.\n       " << message);               \
    }                                                                                          \
}                                                                                              \
while (false)

do                                                                                             \
{                                                                                              \
    if (!(condition)) [[unlikely]]                                                             \
    {                                                                                          \
        NF_THROW("Assertion `" #condition "` failed.\n       " << message);                    \
    }                                                                                          \
}                                                                                              \
while (false)
